SELECT ST.NAME,ST.BRANCH FROM STUDENT AS ST, INTERVIEWROUND AS IR, COMPANY AS CO WHERE ST.ID=IR.STUDENTID AND IR.COMPANYID=CO.ID AND IR.PLACED=1 AND ST.ID IN (SELECT ST.ID FROM STUDENT AS ST, INTERVIEWROUND AS IR, COMPANY AS CO WHERE ST.ID=IR.STUDENTID AND IR.COMPANYID=CO.ID AND IR.PLACED=0) ORDER BY ST.NAME;